let menu=document.querySelector(".nav__menu"),icon=document.querySelector(".nav__menu-line"),list=document.querySelector(".nav__list");menu.addEventListener("click",()=>{icon.className.includes("nav__menu-line")?(icon.classList.replace("nav__menu-line","nav__menu-cross"),list.classList.add("opened")):(icon.classList.replace("nav__menu-cross","nav__menu-line"),list.classList.remove("opened"))});